  passport.use('local-signup', new LocalStrategy({
    usernameField: 'email',
    passwordField: 'password',
    passReqToCallback: true,
    session: true
  }, function (req, email, password, done) {
    db.query("SELECT * FROM user WHERE email = ?", [email], function (err, rows) {
      console.log(rows);
