        return done(null, false, req.flash('signupMessage', 'That username is already taken.'));
      } else {
        var newUserMysql = {
          email: email,
          password: bcrypt.hashSync(password, null, null),
          username: req.body.username
        };
        console.log(newUserMysql);
        db.query("INSERT INTO user ( email, password, username ) values (?,?,?)", newUserMysql, function (err, rows) {
          // newUserMysql.email = rows.username;
          return done(null, newUserMysql);
        });
      }
    });
  }));
  passport.use('local-login', new LocalStrategy({
    // by default, local strategy uses username and password, we will override with email
    usernameField: 'email',
    passwordField: 'password',
    passReqToCallback: true // allows us to pass back the entire request to the callback

  }, function (req, username, password, done) {
    // callback with email and password from our form
    db.query("SELECT * FROM user WHERE email = ?", [username], function (err, rows) {
      if (err) return done(err);

      if (!rows.length) {
        return done(null, false, req.flash('loginMessage', 'No user found.')); // req.flash is the way to set flashdata using connect-flash
      } // if the user is found but the password is wrong


      if (!bcrypt.compareSync(password, rows[0].password)) return done(null, false, req.flash('loginMessage', 'Oops! Wrong password.')); // create the loginMessage and save it to session as flashdata
      // all is well, return successful user

      console.log(rows[0]);
      return done(null, rows[0]);
    });
  }));
}; // expose this function to our app using module.exports
