        res.render('list', {
          currentUser,
          list: rows
        });
      } else {
        res.render('list', {
          list: rows
        });
      }
    });
  });
  router.get('/outer', (req, res) => {
    var clothes;
    db.query("SELECT * FROM clothes WHERE category = ?", "outer", (err, rows) => {
      clothes = rows;

      if (req.session.passport) {
        var currentUser = req.session.passport.user;
        res.render('list', {
          currentUser,
          list: rows
        });
      } else {
        res.render('list', {
          list: rows
        });
      }
    });
  });
  router.get('/detail/:id', (req, res) => {
